mapper.select:
select CONCAT(menu_name,'(', GROUP_CONCAT(code_text), ')') from sm_role_menu rm join sm_menu m on rm.menu_id=m.row_id join md_dic d on rm.permission_=d.code_ join md_dic_index di on d.index_id=di.row_id and di.key_='CRUD'
rm.role_id=#{roleId}
group by menu_name order by menu_id

select menu_id as menuId, permission_ as permission from sys_role_menu where 1=1 and DELETED_FLAG = '0'
and role_id=#{roleId}
order by menu_id

mapper.delete:
 update sys_role_menu set deleted_flag = 1 


		and role_id=#{roleId}


		and menu_id=#{menuId}


		and permission_=#{permission}


		and row_id=#{rowId}
