mapper.select:
select CONCAT(menu.permission_,'.',um.permission_) permission from sm_menu menu join sm_user_menu um on um.menu_id=menu.row_id where length(menu.permission_)>0
and um.user_id=#{userId}
union select CONCAT(menu.permission_,'.',rm.permission_) permission from sm_menu menu join sm_role_menu rm on rm.menu_id=menu.row_id join sm_user_role ur on ur.role_id=rm.role_id where length(menu.permission_)>0
and ur.user_id=#{userId}
